	import SynchronousCommand from "../../ace/SynchronousCommand";
	import Event from "../../ace/Event";
	import UpdateTodoAction from "../../../src/todo/actions/UpdateTodoAction";
	import * as AppUtils from "../../../src/AppUtils";
	import * as AppState from "../../../src/AppState";
	
	export default class AbstractEditedTodoKeyPressedCommand extends SynchronousCommand {
	    constructor() {
	        super("todo.EditedTodoKeyPressedCommand");
	    }
	
	    initCommandData(data) {
	        data.todoList = AppState.get(
	        	["container", "todos", "todoList"]
	        )
	        ;
	        data.outcomes = [];
	    }

		addEnterOutcome(data) {
			data.outcomes.push("enter");
		}
		addEscOutcome(data) {
			data.outcomes.push("esc");
		}
		addNotEnterOutcome(data) {
			data.outcomes.push("notEnter");
		}
		
		publishEvents(data) {
			const events = [];
			const actionsToBeTriggered = [];
			if (data.outcomes.includes("enter")) {
				events.push(new Event('todo.EditedTodoKeyPressedEnterEvent'));
				actionsToBeTriggered.push(
					{
						action: new UpdateTodoAction(), 
						data: {
							id: data.id
						}
					}
				);
			}
			if (data.outcomes.includes("esc")) {
				events.push(new Event('todo.EditedTodoKeyPressedEscEvent'));
			}
			
			this.publish(events, data);
		  	AppState.stateUpdated();
			this.trigger(actionsToBeTriggered);
	
		}

	}
	
	
	
