import AsynchronousCommand from "../../ace/AsynchronousCommand";
import Event from "../../ace/Event";
import TriggerAction from "../../ace/TriggerAction";
import * as AppUtils from "../../../src/app/AppUtils";
import GetTodoListAction from "../../../src/todo/actions/GetTodoListAction";

export default class AbstractUpdateTodoCommand extends AsynchronousCommand {
    constructor() {
        super("todo.UpdateTodoCommand");
    }
    
    initCommandData(data) {
        data.id = AppUtils.get(
        	["container", "todos", "editedTodoId"]
        );
        data.description = AppUtils.get(
        	["container", "todos", "editedDescription"]
        );
        data.outcomes = [];
    }

	addOkOutcome(data) {
		data.outcomes.push("ok");
	}
	addEmptyOutcome(data) {
		data.outcomes.push("empty");
	}

	execute(data) {
	    return new Promise((resolve, reject) => {
	    	let payload = {
	    		id : data.id,
	    		description : data.description
	    	};
			AppUtils.httpPut(`${AppUtils.settings.rootPath}/todos/update`, data.uuid, false, payload).then(() => {
				this.handleResponse(data, resolve, reject);
			}, (error) => {
				data.error = error;
				this.handleError(data, resolve, reject);
			});
	    });
	}

    publishEvents(data) {
		if (data.outcomes.includes("ok")) {
			new Event('todo.UpdateTodoOkEvent').publish(data);
			AppUtils.stateUpdated();
			new TriggerAction().publish(
				new GetTodoListAction(), 
					{
					}
			)
		}
		if (data.outcomes.includes("empty")) {
			new Event('todo.UpdateTodoEmptyEvent').publish(data);
			AppUtils.stateUpdated();
		}
    }

}



